import { Component, EventEmitter, Input, Output } from '@angular/core';

@Component({
  selector: 'app-confirm-dialog',
  templateUrl: './confirm-dialog.component.html',
  styleUrls: ['./confirm-dialog.component.scss'],
})
export class ConfirmDialogComponent {
  @Input() mensaje = '¿Está seguro de eliminar el usuario seleccionado?';
  @Output() onConfirmar = new EventEmitter<void>();
  @Output() onCancelar = new EventEmitter<void>();

  confirmar(): void {
    this.onConfirmar.emit();
  }

  cancelar(): void {
    this.onCancelar.emit();
  }
}
<div class="modal-content">
  <div class="modal-body text-center p-4">
    <p>{{ mensaje }}</p>
    <div class="mt-4">
      <button type="button" class="btn btn-primary me-2" (click)="confirmar()">
        Aceptar
      </button>
      <button type="button" class="btn btn-light" (click)="cancelar()">
        Cancelar
      </button>
    </div>
  </div>
</div>
