<div class="container-fluid p-0">
  <div class="p-3 bg-white">
    <div class="text-muted small">Módulo de Administración</div>
    <h2>Administración de usuarios</h2>

    <div class="row mb-4">
      <div class="col-md-4">
        <select
          class="form-select"
          [(ngModel)]="departamentoSeleccionado"
          (change)="aplicarFiltros()"
        >
          <option [ngValue]="null">Seleccione un Departamento</option>
          <option *ngFor="let dept of departamentos" [ngValue]="dept">
            {{ dept.nombre }}
          </option>
        </select>
      </div>
      <div class="col-md-4">
        <select
          class="form-select"
          [(ngModel)]="cargoSeleccionado"
          (change)="aplicarFiltros()"
        >
          <option [ngValue]="null">Seleccione un Cargo</option>
          <option *ngFor="let cargo of cargos" [ngValue]="cargo">
            {{ cargo.nombre }}
          </option>
        </select>
      </div>
      <div class="col-md-4 text-end">
        <button class="btn btn-light" (click)="abrirModalCrear()">
          Crear nuevo usuario
        </button>
      </div>
    </div>

    <div class="card">
      <div class="table-responsive">
        <table class="table">
          <thead>
            <tr>
              <th>Usuario</th>
              <th>Nombres</th>
              <th>Apellidos</th>
              <th>Departamento</th>
              <th>Cargo</th>
              <th>Email</th>
              <th>Acciones</th>
            </tr>
          </thead>
          <tbody>
            <tr *ngFor="let usuario of usuariosFiltrados">
              <td>{{ usuario.usuario }}</td>
              <td>{{ usuario.primer_nombre }} {{ usuario.segundo_nombre }}</td>
              <td>
                {{ usuario.primer_apellido }} {{ usuario.segundo_apellido }}
              </td>
              <td>{{ usuario.departamento?.nombre }}</td>
              <td>{{ usuario.cargo?.nombre }}</td>
              <td>{{ usuario.email }}</td>
              <td>
                <button
                  class="btn btn-sm text-success"
                  (click)="abrirModalEditar(usuario)"
                >
                  <i class="fas fa-edit"></i> Editar
                </button>
                <button
                  class="btn btn-sm text-danger ms-2"
                  (click)="abrirModalConfirmacion(usuario)"
                >
                  <i class="fas fa-trash"></i> Eliminar
                </button>
              </td>
            </tr>
          </tbody>
        </table>
      </div>
      <div class="card-footer bg-light">
        <small class="text-muted"
          >Total Registros: {{ usuariosFiltrados.length }}</small
        >
      </div>
    </div>
  </div>
</div>
